mapper.select:
select

from c_order


		product_id = #{productId}


		and item_id = #{itemId}


		and shop_id = #{shopId}


		and city_id = #{cityId}


		and user_name = #{userName}


		and phone = #{phone}


		and order_status = #{status}


		and coupon_code = #{couponCode}


		and create_time > #{createDateFrom}


		and create_time < #{createDateTo}


order by create_time desc limit ${start}, ${limit}

select count(*) from c_order


		product_id = #{productId}


		and item_id = #{itemId}


		and shop_id = #{shopId}


		and city_id = #{cityId}


		and user_name = #{userName}


		and phone = #{phone}


		and order_status = #{status}


		and coupon_code = #{couponCode}


		and create_time > #{createDateFrom}


		and create_time < #{createDateTo}
